// --------------------------- validate ---------------------------  
    $.validator.addMethod('filesize', function(value, element, param) {
        return this.optional(element) || (element.files[0].size <= param) 
    });

    // validate spesial character
    $.validator.addMethod('cek_special_character', function(value, element) {
        // var regex = new RegExp(/[~`!#$%\^&*+=\\[\]\\';,.()-//{}|\\":<>]/, 'gi');
        var regex = new RegExp(/[~`!#$%\^*+=\\[\]\\;,.-//{}|\\":<>]/, 'gi');

        if (regex.test(value)) {
            result = false;
        } else {
            result = true;
        }
        return result;
    });
    
    // validation phone
    $('.val-telp').on('input propertychange paste', function (e) {
        var val = $(this).val()
        var reg = /^0/gi;
        if (val.match(reg)) {
            $(this).val(val.replace(reg, ''));
        }
    });

    $('.val-username').on('keyup keypress input propertychange paste', function (e) {
        var val = $(this).val();

        // convert title
        x = val.toLowerCase().replace(/\s/g, '-');
        x = x.replace(/[^a-zA-Z0-9_]/g, '');
        
        $(this).val(x);
    });

    $('.val-referal').on('keyup keypress input propertychange paste', function (e) {
        var val = $(this).val();

        // convert title
        x = val.toUpperCase().replace(/\s/g, '-');
        x = x.replace(/[^a-zA-Z0-9]/g, '');
        
        $(this).val(x);
    });

    $('.val-percent').on('keyup keypress input propertychange paste', function (e) {
        var val = $(this).val();

        if (Number(val) > 100) {
            x = val = 100;   
            $(this).val(x);
        } 
    });
// --------------------------- end validate ---------------------------


// --------------------------- delete data multiple ---------------------------
    $('#delete-data-multiple').on('click', function() {
    	$('#modal-delete').modal('show');
    });

    $(document).on("change", ".check-record", function() {
    	create_url_delete();
    });

    $('input[name=check-all-record]').change(function() {
    
        var check_all = false;
        
    	if ($(this).is(':checked')) {
    		check_all = true;
    	}

    	$('#table-data > tbody > tr > td > label > input[name=check-record]').each(function() {
    		$(this).prop('checked', check_all);
        });
        
    	create_url_delete();
    });

    $('#table-data > tbody').bind("DOMSubtreeModified", function() {

        var checkall = $('#table-data > thead').children().find('input[name=check-all-record]');
        
    	if (checkall.is(':checked')) {
    		checkall.prop('checked', false);
    	}
    });

    function create_url_delete() {

    	var checkbox_checked = [];

    	$('#table-data > tbody > tr > td > label > input[name=check-record]').each(function() {

    		if ($(this).is(':checked')) {
    			checkbox_checked.push($(this).val());
    		}
    	});

    	var id_str = (JSON.stringify(checkbox_checked));

    	if (checkbox_checked.length > 0) {

    		$('#delete-data-multiple').removeClass('invisible');
    		$('#id3').val(id_str);
    		$('#method').val('multiple');
        }
        else {

    		$('#delete-data-multiple').addClass('invisible', true);
    		$('#id3').val(id_str);
    		$('#method').val('multiple');
    	}
    }
// --------------------------- end delete data multiple ---------------------------


// --------------------------- kurasi data multiple ---------------------------
    $('#kurasi-data-multiple').on('click', function() {
        $('#modal-kurasi').modal('show');
        var param = $(this).attr('data');
        if (param == 'kurasi') {
            $('.modal-title').html('Kurasi Data User');
            $('.modal-des').html('Apakah anda yakin ingin mengkurasi data ini?');
        } else {
            $('.modal-title').html('Batalkan Kurasi');
            $('.modal-des').html('Apakah anda yakin ingin membatalkan kurasi data ini?');
        }
    });

    $(document).on("change", ".check-record", function() {
        create_url_kurasi();
    });

    $('input[name=check-all-record]').change(function() {
    
        var check_all = false;
        
        if ($(this).is(':checked')) {
            check_all = true;
        }

        $('#table-data > tbody > tr > td > label > input[name=check-record]').each(function() {
            $(this).prop('checked', check_all);
        });
        
        create_url_kurasi();
    });

    $('#table-data > tbody').bind("DOMSubtreeModified", function() {

        var checkall = $('#table-data > thead').children().find('input[name=check-all-record]');
        
        if (checkall.is(':checked')) {
            checkall.prop('checked', false);
        }
    });

    function create_url_kurasi() {

        var checkbox_checked = [];

        $('#table-data > tbody > tr > td > label > input[name=check-record]').each(function() {

            if ($(this).is(':checked')) {
                checkbox_checked.push($(this).val());
            }
        });

        var id_str = (JSON.stringify(checkbox_checked));

        if (checkbox_checked.length > 0) {

            $('#kurasi-data-multiple').removeClass('invisible');
            $('#id3kurasi').val(id_str);
        }
        else {

            $('#kurasi-data-multiple').addClass('invisible', true);
            $('#id3kurasi').val(id_str);
        }
    }
// --------------------------- end kurasi data multiple ---------------------------


